import "./Card.css";
import React from 'react';

const style = {
    backgroundColor: props.color || '#F00'
}   

export default (props) => {
    return (

        <div className="Card" style={style}>
            <div className="Title">{props.titulo}</div>
            <div className="Content">
                {props.children}
            </div>
        </div>
    );
};
